package condition.ex01;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// random > Math class
		/*
		 * 0.0 <= random() < 1.0
		 * 0.0 <= random() * 6 < 6.0
		 * 0 <= (int)(random() * 6) < 6
		 * 1 <= (int)(random() * 6) < 7
		 * >> 1 ~ 6까지의 정수를 랜덤으로 뽑아냄 *********
		 * 교수님 난수 쓰는 거 좋아하심!!!!!!!!!!!!!
		 */
		int num = (int)(Math.random() * 6) + 1;
		if (num == 1) {
			System.out.println("1");
		} else if (num == 2) {
			System.out.println("2");
		} else if (num == 3) {
			System.out.println("3");
		} else if (num == 4) {
			System.out.println("4");
		} else if (num == 5) {
			System.out.println("5");
		} else {
			System.out.println("6");
		}
	}

}
